import { Component, OnInit } from '@angular/core';
import { MatExpansionModule } from '@angular/material/expansion';

@Component({
  selector: 'app-sobre',
  templateUrl: './sobre.component.html',
  styleUrls: ['./sobre.component.css'],
})
export class SobreComponent implements OnInit {
  constructor() {}

  ngOnInit(): void {
    this.getData();
  }

  showData = [];
  qntEstados = 0
  imagensRegiao = [
    {"nome": "Norte", "imagem": "data:image/jpeg;base64,"},
    {"nome": "Nordeste", "imagem": "data:image/jpeg;base64,"},
    {"nome": "Centro-Oeste", "imagem": "data:image/jpeg;base64,"},
    {"nome": "Sudeste", "imagem": "data:image/jpeg;base64,"},
    {"nome": "Sul", "imagem": "data:image/jpeg;base64,"},
  ]

  async getData() {
    let estados: any = [];
    let data: any = [];
    let dataSanitizada: any = [];

    data = await fetch(
      'https://servicodados.ibge.gov.br/api/v1/localidades/estados'
    ).then((res: any) => {
      let dados: [] = res.json();
      return dados;
    });
    this.qntEstados = data.length
    //ordenar array
    data.sort((a: any, b: any) => {
      if (a['nome'] < b['nome']) {
        return -1;
      } else {
        return 1;
      }
    });
    data.map((res: any) => {
      if (!estados.includes(res['regiao']['nome'])) {
        estados.push(res['regiao']['nome']);
      }
    });
    const regioes = estados.map((resEstados: any) => {
      const filter = data.filter(
        (resData: any) => resEstados === resData['regiao']['nome']
      );
      const imagem = this.imagensRegiao.filter((resImagem: any) => resImagem.nome === resEstados)
      dataSanitizada.push({ 'regiao': resEstados, 'filtro': filter, "imagem": imagem });
    });
    this.showData = dataSanitizada
  }
}
